// src/components/Navbar.js
import React, { useState, useEffect } from 'react'; // Importa useState y useEffect
import { Link, useNavigate } from 'react-router-dom';
import api from '../services/api';

const Navbar = () => {
  const [userRole, setUserRole] = useState(''); // Guardar el rol del usuario

  const navigate = useNavigate();

  // Extraer el rol del token al cargar el componente
  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) {
      navigate('/login'); // Redirigir si no hay token
      return;
    }

    const { rol } = JSON.parse(atob(token.split('.')[1])); // Decodificar token para extraer el rol
    setUserRole(rol); // Guardar el rol del usuario
  }, [navigate]);

  const handleLogout = () => {
    // Eliminar el token del almacenamiento local y redirigir al login
    localStorage.removeItem('token');
    navigate('/login');
  };

  return (
    <nav className="navbar navbar-expand-lg navbar-dark bg-dark">
      <div className="container-fluid">
        <Link className="navbar-brand" to="/dashboard">
          Gestión de Activos Fijos
        </Link>

        <button
          className="navbar-toggler"
          type="button"
          data-bs-toggle="collapse"
          data-bs-target="#navbarNav"
          aria-controls="navbarNav"
          aria-expanded="false"
          aria-label="Toggle navigation"
        >
          <span className="navbar-toggler-icon"></span>
        </button>

        <div className="collapse navbar-collapse" id="navbarNav">
          <ul className="navbar-nav me-auto mb-2 mb-lg-0">
            {(userRole === 'admin') && (
              <li className="nav-item">
                <Link className="nav-link" to="/areas">
                  Áreas
                </Link>
              </li>
            )}
            {(userRole === 'admin') && (
              <li className="nav-item">
                <Link className="nav-link" to="/users">
                  Usuarios
                </Link>
              </li>
            )}
            {(userRole === 'admin') && (
              <li className="nav-item">
                <Link className="nav-link" to="/tipos-activos-fijos">
                  Tipos de Activos Fijos
                </Link>
              </li>
            )}
            {(userRole === 'admin' || userRole === 'editor' || userRole === 'usuario') && (
              <li className="nav-item">
                <Link className="nav-link" to="/activos-fijos">
                  Activos Fijos
                </Link>
              </li>
            )}
            {(userRole === 'admin' || userRole === 'editor') && (
              <li className="nav-item">
                <Link className="nav-link" to="/registro_activo">
                  Registros de Activos
                </Link>
              </li>
            )}
            {(userRole === 'admin' || userRole === 'editor') && (
              <li className="nav-item">
                <Link className="nav-link" to="/company-clientes">
                  Compañías Clientes
                </Link>
              </li>
            )}
            {(userRole === 'admin' || userRole === 'editor') && (
              <li className="nav-item">
                <Link className="nav-link" to="/email-config">
                  Configuración de Correo
                </Link>
              </li>
            )}
            {(userRole === 'admin' || userRole === 'editor') && (
              <li className="nav-item">
                <Link className="nav-link" to="/ubicaciones">
                  Ubicaciones
                </Link>
              </li>
            )}
          </ul>
          ?¡
          <button className="btn btn-outline-light" onClick={handleLogout}>
            Cerrar Sesión
          </button>
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
